form.script:
  $(function () {

    var reason_disagree = $( "#reason_disagree" ).val();
    if ( reason_disagree === "not_agree" ) {                                    
       $("#disagreereson").show();                                   
      }
    if ( reason_disagree === "fully" ) {                                    
       $("#disagreereson").hide();                                   
      }
    if ( reason_disagree === "partially" ) {                                    
       $("#disagreereson").hide();                                   
      }

    var full_reason_disagree = $( "#full_reason_disagree" ).val();
    if ( full_reason_disagree === "not_agree" ) {                                    
       $("#fulldisagreereson").show();                                   
      }
    if ( full_reason_disagree === "fully" ) {                                    
       $("#fulldisagreereson").hide();                                   
      }
    if ( full_reason_disagree === "partially" ) {                                    
       $("#fulldisagreereson").show();                                   
      }

     var promotion_measure = $( "#promotion_measure" ).val();
    if ( promotion_measure === "no" ) {                                    
       $("#date_effective").hide();  
        $("#promotion_position").hide();  
        $("#promotion_grade").hide();                                    
      }
    if ( promotion_measure === "yes" ) {                                    
       $("#date_effective").show();  
        $("#promotion_position").show();  
        $("#promotion_grade").show();                                    
      }


});
  $(document).ready(function(){

  $("#reason_disagree").change(function(){
    var reason_disagree = $( "#reason_disagree" ).val();
    if ( reason_disagree === "not_agree" ) {                                   
       $("#disagreereson").show();                                   
      } 
     if ( reason_disagree === "fully" ) {                                    
       $("#disagreereson").hide();                                   
      }
    if ( reason_disagree === "partially" ) {                                    
       $("#disagreereson").show();                                   
      }
  });

 $("#full_reason_disagree").change(function(){
    var full_reason_disagree = $( "#full_reason_disagree" ).val();
    if ( full_reason_disagree === "not_agree" ) {                                   
       $("#fulldisagreereson").show();                                   
      } 
     if ( full_reason_disagree === "fully" ) {                                    
       $("#fulldisagreereson").hide();                                   
      }
    if ( full_reason_disagree === "partially" ) {                                    
       $("#fulldisagreereson").show();                                   
      }
  });

$("#promotion_measure").change(function(){
    var promotion_measure = $( "#promotion_measure" ).val();
    if ( promotion_measure === "no" ) {                                   
       $("#date_effective").hide();  
        $("#promotion_position").hide();  
        $("#promotion_grade").hide();  
      } 
     if ( promotion_measure === "yes" ) {                                    
       $("#date_effective").show();  
        $("#promotion_position").show();  
        $("#promotion_grade").show();                                 
      }

  });


});


